INSERT INTO Animes(AnimeID, AnimeTitle, AnimeDesc, TypeID, AiredBegin, AiredEnd, Premiered, Duration, PosterUrl) VALUES (1864, CONCAT('Tayutama: Kiss on My Deity'), CONCAT('Yuuri Mito is a typical, normal Japanese teenager.  He goes to school, works on people', CHAR(39), 's motorcycles and performs exorcisms.  Okay, that last part', CHAR(39), 's a little bit unusual, but his family lives in a shrine and they do that sort of thing.  Still, you would think he', CHAR(39), 'd know enough to be careful with an ancient relic he finds in the woods, especially when a mysterious goddess appears and tells him to leave it alone.  Unfortunately, despite Mito', CHAR(39), 's best efforts, the seal gets broken anyway and a number of dangerous "tayuti" that it held in stasis get loose.  This is bad.  Mito also ends up with a beautiful goddess girl who decides that she', CHAR(39), 's going to marry him.  This might not be so bad. if he wasn', CHAR(39), 't already caught up in the middle of a war between the entities he', CHAR(39), 's released.  The flesh may be weak but the spirit', CHAR(39), 's more than willing to compensate. <br><br>(Source: Sentai Filmworks)'), 1, '2009-04-06', '2009-06-22', 'Spring 2009', '23 min. per ep.', 'https://cdn.myanimelist.net/images/anime/2/11972.jpg');
