INSERT INTO Animes(AnimeID, AnimeTitle, AnimeDesc, TypeID, AiredBegin, AiredEnd, Premiered, Duration, PosterUrl) VALUES (399, CONCAT('Dear Boys'), CONCAT('Aikawa Kazuhiko was the captain of Tendoji high school prestigious basketball team. He moves into a new town to attend Mizuho high school and joins its basketball team. However, Mizuho high', CHAR(39), 's basketball team is far from being prestigious, in fact, it', CHAR(39), 's now defunct. Nevertheless to say, Kazuhiko', CHAR(39), 's persistence, passion and basketball skills inspired other team members of the dysfunctional basketball team to gear up and start practicing again.<br><br>The goal is to play in the national tournaments where all young basketball players meet their opponents to compete with them. The tale of youth of the five protagonists: Fujiwara Takumi, Ishii Tsutomu, Dobashi Kenji, Miura Ranmaru and Aikawa Kazuhiko have just began along with the live of Mizuho high school basketball team.<br><br>(Source: AnimeNfo)'), 1, '2003-04-07', '2003-09-29', 'Spring 2003', '24 min. per ep.', 'https://cdn.myanimelist.net/images/anime/1141/109328.jpg');
