INSERT INTO Animes(AnimeID, AnimeTitle, AnimeDesc, TypeID, AiredBegin, AiredEnd, Premiered, Duration, PosterUrl) VALUES (683, CONCAT('Haitai Nanafa'), CONCAT('Nanafa Kyan lives in Okinawa with her grandmother who runs the "Kame Soba" soba shop, her beautiful older sister Nao who is in high school, and her younger sister Kokona, who is in elementary school and has a strong ability to sense the supernatural.<br><br>One day, Nanafa witnesses a seal fall off of a Chinese banyan tree, and three spirits who live in that tree are unleashed. These spirits include Niina and Raana, who are "jimunaa" spirits. The third spirit is Iina, who is an incarnation of an Okinawan lion statue. As spirits start appearing one after another, the peaceful life of Nanafa and her family begins to change.<br><br>(Source: ANN)'), 4, '2012-10-06', '2012-12-29', 'Fall 2012', '3 min. per ep.', 'https://cdn.myanimelist.net/images/anime/12/45991.jpg');
